"""
Solution for Word Search, Time O(n).

Idea:
DFS.
"""

from __future__ import annotations

# Solution.
class Solution:
    def exist(self, board: List[List[str]], word: str) -> bool:
        def dfs(row_i, col_i, word_i, visited):
            if row_i < 0 or row_i >= row_num or col_i < 0 or col_i >= col_num or (row_i, col_i) in visited or board[row_i][col_i] != word[word_i]:  # Exceeding boundary, visited before, or not a match.
                return False
            if word_i == len(word) - 1:  # Find the last char.
                return True
            visited.add((row_i, col_i))
            find = dfs(row_i - 1, col_i, word_i + 1, visited) or dfs(row_i + 1, col_i, word_i + 1, visited) or dfs(row_i, col_i - 1, word_i + 1, visited) or dfs(row_i, col_i + 1, word_i + 1, visited)
            visited.remove((row_i, col_i))
            return find

        if not word:
            return True
        if not board or not board[0]:
            return False
        row_num = len(board)
        col_num = len(board[0])
        visited = set()
        for row_i in range(row_num):
            for col_i in range(col_num):
                if dfs(row_i, col_i, 0, visited):
                    return True
        return False

